mapper.select:
select count(*) from blog_tag_rel
where blog_id = #{blogId,jdbcType=VARCHAR}
and tag_id = #{tagId,jdbcType=VARCHAR}

mapper.delete:
delete from blog_tag_rel
where blog_id = #{blogId,jdbcType=VARCHAR}
and tag_id = #{tagId,jdbcType=VARCHAR}

mapper.select:
select
tag_id from blog_tag_rel
where blog_id = #{blogId,jdbcType=VARCHAR}
